//Insert a number 'delimeter' between every two consecutive elements of input array `numbers'
// >>> intersperse([], 4)
// []
// >>> intersperse([1, 2, 3], 4)
// [1, 4, 2, 4, 3]
function intersperse(numbers, delimeter){ 
    let result = [];
    for (let i = 0; i < numbers.length; i++) {
        if (i !== 0) { // skip the first element
            result.push(delimeter);
        }
        result.push(numbers[i]);
    }
    return result;
}

//Input to this function is a string represented multiple groups for nested parentheses separated by spaces.
// For each of the group, output the deepest level of nesting of parentheses.
// E.g. (()()) has maximum two levels of nesting while ((())) has three.
// >>> parse_nested_parens("(()()) ((())) () ((())()())")
// [2, 3, 1, 3]
function parse_nested_parens(paren_string){ 
    let result = [];

    // Splitting the string into individual groups
    paren_string.split(' ');

    // Iterating through each group
    for(let i=0; i<paren_string.length; i++){
        // If the current group is empty then continue
        if(paren_string[i] == '') continue;

        // Checking if the current group is an opening parenthesis
        if(paren_string[i] == '('){
            // Adding the depth of the current group to the result array
            result.push(0);
        }
        // Checking if the current group is a closing parenthesis
        else{
            // Subtracting the depth of the previous group from the current group
            result[result.length-1]-= (i+1 - result[result.length-2]);
        }
    }

    return result;
}<|endoftext|>

