//Return true if a given number is prime, and false otherwise.
// >>> is_prime(6)
// false
// >>> is_prime(101)
// true
// >>> is_prime(11)
// true
// >>> is_prime(13441)
// true
// >>> is_prime(61)
// true
// >>> is_prime(4)
// false
// >>> is_prime(1)
// false
function is_prime(n){ 
    // If n is less than 2, it's not prime
    if (n < 2) return false;

    // Check for factors from 2 to the square root of n
    for(let i = 2; i <= Math.sqrt(n); i++){
