// >>> change_base(8, 2)
// "1000"
// >>> change_base(7, 2)
// "111"
function change_base(x, base){ 
    let result = ""; 
    while (x > 0) { 
        let remainder = x % base; 
        result += remainder.toString(); 
        x = Math.floor(x / base); 
    } 

    // Reverse the result
    result = result.split('').reverse().join(''); 

    return result; 
}

