// >>> median([3, 1, 2, 4, 5])
// 3
// >>> median([-10, 4, 6, 1000, 10, 20])
// 15.0
function median(l){ 
    // Sort the list
    let sorted = l.sort((a,b) => a-b);

    // If length is odd then return the middle element
    if(sorted.length % 2 !== 0){
        return sorted[Math.floor(sorted.length/2)];
    }
    // Else calculate the average of two middle elements
    else{
        return (sorted[Math.floor(sorted.length/2)-1] + sorted[Math.floor(sorted.length/2)]) / 2;
    }
