//Return 2^n modulo p (be aware of numerics).
// >>> modp(3, 5)
// 3
// >>> modp(1101, 101)
// 2
// >>> modp(0, 101)
// 1
// >>> modp(3, 11)
// 8
// >>> modp(100, 101)
// 1
function modp(n, p){ 
    if(p == 1) return n; // If p is 1, the result is n itself.

    let res = 1;
    while(n > 0){
        if(n % 2 === 1){
            res *= p; // Multiply p to the power of n/2.
        }
        n = Math.floor(n / 2); // Divide n by 2.
        p = Math.floor(p * p); // Multiply p by itself.
    }
    return res % p; // Return the result modulo p.
}<|endoftext|>

