// The extra characters are there to help with the format.
// Examples:
// >>> decimal_to_binary(15)
// "db1111db"
// >>> decimal_to_binary(32)
// "db100000db"
function decimal_to_binary(decimal){ 
    let result = "";
    while (decimal > 0) {
        let remainder = Math.floor(decimal % 2);
        if (remainder === 1) {
            result += "db";
        }
        decimal = Math.floor(decimal / 2);
    }
    return result;
}

