// true
// >>> is_happy("aabb")
// false
// >>> is_happy("adb")
// true
// >>> is_happy("xyy")
// false
function is_happy(s){ 
    let charCount = {};
    for (let i = 0; i < s.length - 2; i++) {
        if (!charCount[s[i]]) {
            charCount[s[i]] = 1;
        } else {
            return false;
        }
    }

    for (let j = 2; j < s.length; j++) {
        if (charCount[s[j - 2] + s[j - 1] + s[j]] > 0) {
            return false;
        }
