function is_sorted(lst){ 
    // Check if length of list is less than 2
    if (lst.length < 2) {
        return true;
    }

    // Compare first two elements
    let first = lst[0];
    let second = lst[1];

    // Check if first element is greater than second
    if (first > second) {
        return false;
    }

    // Compare remaining elements
    for(let i = 2; i < lst.length; i++) {
        if (lst[i] <= first) {
            return false;
        } else {
            first = lst[i];
        }
    }

    return true;
