// first n + 1 numbers of the Tribonacci sequence.
// Examples:
// >>> tri(3)
// [1, 3, 2, 8]
function tri(n){ 
    let arr = [];
    for(let i=0; i<n+1; i++){
        if(i===0){
            arr.push(1);
        } else if(i===1){
            arr.push(3);
        } else if(i%2 === 0){
            arr.push(arr[i-1] + arr[i-2]);
        } else {
            arr.push(arr[i-1] + arr[i-2] + arr[i-3]);
        }
    }
