//Create a function that takes a string as input which contains only square brackets.
// The function should return true if and only if there is a valid subsequence of brackets 
// where at least one bracket in the subsequence is nested.
// >>> is_nested("[[]]")
// true
// >>> is_nested("[]]]]]]][[[[[]")
// false
// >>> is_nested("[][]")
// false
// >>> is_nested("[]")
// false
// >>> is_nested("[[][]]")
// true
// >>> is_nested("[[]][[")
// true
function is_nested(string){  
  let stack = [];
  for (let i = 0; i < string.length; i++) {
    if (string[i] === '[') { 
      stack.push(i);
    } else if (stack.length > 0 && string[i] === ']') {
      stack.pop();
    }
  }

  // Check if the stack has more than 1 element
  if (stack.length > 1) {
    return false;
  }

  // Check if the last element in the stack is inside the brackets
  return stack[stack.length - 2] <= stack.length - 1;
}

//You are given an array of numbers.
// You need to return the sum of squared numbers in the given array,
// round each element in the array to the upper int(Ceiling) first.
// Examples:
// >>> lst([1.0, 2.0, 3.0])
// 14
// >>> lst([1.0, 4.0, 9.0])
// 98
// >>> lst([1.0, 3.0, 5.0, 7.0])
// 84
// >>> lst([1.4, 4.2, 0.0])
// 29
// >>> lst([-2.4, 1.0, 1.0])
// 6
function sum_squares(lst){ 
    let sum = 0;
    for (let i = 0; i < lst.length; i++) {
        if (isNaN(lst[i])) {
            continue;
        }
        let num = Math.ceil(Math.pow(lst[i], 2));
        sum += num;
    }
    return sum;
}

