// [undefined, 1]
// >>> largest_smallest_integers([])
// [undefined, undefined]
// >>> largest_smallest_integers([0])
// [undefined, undefined]
function largest_smallest_integers(lst){ 
    if (lst.length === 0) {
        return [undefined, undefined];
    }

    let max = Math.max(...lst);
