// [1, 2]
function even_odd_count(num){ 
    let countEven = 0;
    let countOdd = 0;

    while (num > 0) {
        const digit = num % 10;
        if (digit % 2 === 0) {
            countEven++;
        } else {
            countOdd++;
        }
        num = Math.floor(num / 10);
    }

