//Given the lengths of the three sides of a triangle. Return true if the three
// sides form a right-angled triangle, false otherwise.
// A right-angled triangle is a triangle in which one angle is right angle or 
// 90 degree.
// Example:
// >>> right_angle_triangle(3, 4, 5)
// true
// >>> right_angle_triangle(1, 2, 3)
// false
function right_angle_triangle(a, b, c){  
    // Check for valid input
    if (a <= 0 || b <= 0 || c <= 0) {
        return false;
    }

    // Calculate the square of each side
    let s = Math.sqrt(Math.pow(a, 2) + Math.pow(b, 2) + Math.pow(c, 2));

    // If the square of the longest side is equal to the sum of squares of the other two sides,
    // then it forms a right-angled triangle
    return s === Math.pow(a, 2) + Math.pow(b, 2) + Math.pow(c, 2);
}

