// * 0 <= need <= 1000
// * 0 <= remaining <= 1000
// Have fun :)
function eat(number, need, remaining){ 
    let result = [];
    for(let i=0; i<number; i++){
        result.push(Math.floor((Math.random()*(remaining+1)))+1);
    }
    return result;
