// >>> parse_nested_parens(("(()()) ((())) () ((())()())"))
// (std::vector<long>({(long)2, (long)3, (long)1, (long)3}))
std::vector<long> parse_nested_parens(std::string paren_string) { 
    std::vector<long> res;
    int max = 0;
    int current = 0;
    for(int i = 0; i < paren_string.length(); i++) {
        if(paren_string[i] == '(') {
            current++;
            if(current > max) {
                max = current;
            }
        } else if(paren_string[i] == ')') {
            current--;
        } else if(paren_string[i] == ' ') {
            res.push_back(max);
            max = 0;
        }
    }
    return res;

