// Given vector of numbers (of at least two elements), apply a linear transform to that vector,
// such that the smallest number will become 0 and the largest will become 1
// >>> rescale_to_unit((std::vector<float>({(float)1.0f, (float)2.0f, (float)3.0f, (float)4.0f, (float)5.0f})))
// (std::vector<float>({(float)0.0f, (float)0.25f, (float)0.5f, (float)0.75f, (float)1.0f}))
std::vector<float> rescale_to_unit(std::vector<float> numbers) { 
    float min = *std::min_element(numbers.begin(), numbers.end());
    float max = *std::max_element(numbers.begin(), numbers.end());
    std::vector<float> rescaled;
    for(auto n : numbers){
        rescaled.push_back((n - min)/(max - min));
    }
    return rescaled;

