    for(int i = 0; i < lst.size(); i++){
        for(int j = 0; j < lst[i].size(); j++){
            if(lst[i][j] == x){
                ans.push_back({i, j});
            }
        }
    }
    std::sort(ans.begin(), ans.end());
    for(int i = 0; i < ans.size(); i++){
        if(i + 1 < ans.size() && std::get<0>(ans[i]) == std::get<0>(ans[i + 1])){
            std::swap(ans[i], ans[i + 1]);
        }
    }
    return ans;

