// Examples:
// >>> encode(("test"))
// ("TGST")
// >>> encode(("This is a message"))
// ("tHKS KS C MGSSCGG")
std::string encode(std::string message) { 
    std::string ans = "";
    for(int i=0; i<message.length(); i++) {
        if(message[i] >= 'a' && message[i] <= 'z') {
            char ch = message[i];
            ch = toupper(ch);
            ans += ch;
        }
        else if(message[i] >= 'A' && message[i] <= 'Z') {
            char ch = message[i];
            ch = tolower(ch);
            ans += ch;
        }
        else {
            ans += message[i];
        }
    }
    for(int i=0; i<ans.length(); i++) {
        if(ans[i] == 'a') {
            ans[i] = 'c';
        }
        else if(ans[i] == 'e') {
            ans[i] = 'g';
        }
        else if(ans[i] == 'i') {
            ans[i] = 'k';
        }
        else if(ans[i] == 'o') {
            ans[i] = 'q';
        }
        else if(ans[i] == 'u') {
            ans[i] = 'w';
        }
    }
    return ans;

