bool check_dict_case(std::map<std::string,std::string> dict) { 
    bool lower = true;
    bool upper = true;
    for(auto it = dict.begin(); it != dict.end(); ++it){
        if(!islower(it->first[0])){
            lower = false;
        }
        if(!isupper(it->first[0])){
            upper = false;
        }
    }
    return (lower || upper);

