// Given a string representing a space separated lowercase letters, return a map
// of the letter with the most repetition and containing the corresponding count.
// If several letters have the same occurrence, return all of them.
// Example:
// >>> histogram(("a b c"))
// (std::map<std::string,long>({{"a", 1}, {"b", 1}, {"c", 1}}))
// >>> histogram(("a b b a"))
// (std::map<std::string,long>({{"a", 2}, {"b", 2}}))
// >>> histogram(("a b c a b"))
// (std::map<std::string,long>({{"a", 2}, {"b", 2}}))
// >>> histogram(("b b b b a"))
// (std::map<std::string,long>({{"b", 4}}))
// >>> histogram((""))
// (std::map<std::string,long>())
std::map<std::string,long> histogram(std::string test) { 
    std::map<std::string,long> m;
    if(test.size() == 0)
        return m;
    std::vector<std::string> v;
    std::stringstream ss(test);
    while(ss >> test)
        v.push_back(test);
    int max = 1;
    for(int i = 0; i < v.size(); i++) {
        long count = 1;
        for(int j = i + 1; j < v.size(); j++) {
            if(v[i] == v[j])
                count++;
        }
        if(count > max) {
            m.clear();
            m[v[i]] = count;
            max = count;
        } else if(count == max) {
            m[v[i]] = count;
        }
    }
    return m;

