// Given a vector of strings, where each string consists of only digits, return a vector.
// Each element i of the output should be "the number of odd elements in the
// string i of the input." where all the i's should be replaced by the number
// of odd digits in the i'th string of the input.
// >>> odd_count((std::vector<std::string>({(std::string)"1234567"})))
// (std::vector<std::string>({(std::string)"the number of odd elements 4n the str4ng 4 of the 4nput."}))
// >>> odd_count((std::vector<std::string>({(std::string)"3", (std::string)"11111111"})))
// (std::vector<std::string>({(std::string)"the number of odd elements 1n the str1ng 1 of the 1nput.", (std::string)"the number of odd elements 8n the str8ng 8 of the 8nput."}))
std::vector<std::string> odd_count(std::vector<std::string> lst) { 
    std::vector<std::string> res;
    for(auto &s:lst){
        int cnt=0;
        for(auto c:s){
            if(c%2==1)cnt++;
        }
        std::string t="the number of odd elements ";
        t+=std::to_string(cnt);
        t+="n the str";
        t+=std::to_string(cnt);
        t+="ng ";
        t+=std::to_string(cnt);
        t+=" of the ";
        t+=std::to_string(cnt);
        t+="nput.";
        res.push_back(t);
    }
    return res;

