// Given a vector of integers nums, find the minimum sum of any non-empty sub-vector
// of nums.
// Example
// >>> minSubArraySum((std::vector<long>({(long)2, (long)3, (long)4, (long)1, (long)2, (long)4})))
// (1)
// >>> minSubArraySum((std::vector<long>({(long)-1, (long)-2, (long)-3})))
// (-6)
long minSubArraySum(std::vector<long> nums) { 
    long min_sum = INT_MAX;
    long current_sum = 0;
    for(long i = 0; i < nums.size(); i++) {
        current_sum += nums[i];
        if(current_sum < min_sum) {
            min_sum = current_sum;
        }
        if(current_sum > 0) {
            current_sum = 0;
        }
    }
    return min_sum;

