// Given a string s and a natural number n, you have been tasked to implement 
// a function that returns a vector of all words from string s that contain exactly 
// n consonants, in order these words appear in the string s.
// If the string s is empty then the function should return an empty vector.
// Note: you may assume the input string contains only letters and spaces.
// Examples:
// >>> select_words(("Mary had a little lamb"), (4))
// (std::vector<std::string>({(std::string)"little"}))
// >>> select_words(("Mary had a little lamb"), (3))
// (std::vector<std::string>({(std::string)"Mary", (std::string)"lamb"}))
// >>> select_words(("simple white space"), (2))
// (std::vector<std::string>())
// >>> select_words(("Hello world"), (4))
// (std::vector<std::string>({(std::string)"world"}))
// >>> select_words(("Uncle sam"), (3))
// (std::vector<std::string>({(std::string)"Uncle"}))
std::vector<std::string> select_words(std::string s, long n) { 
    std::vector<std::string> ans;
    int count = 0;
    for(int i = 0; i < s.size(); i++){
        if(s[i] == ' '){
            if(count == n){
                ans.push_back(s.substr(i - n, n));
            }
            count = 0;
        }else{
            if((s[i] >= 'a' && s[i] <= 'z') || (s[i] >= 'A' && s[i] <= 'Z')){
                count++;
            }
        }
    }
    if(count == n){
        ans.push_back(s.substr(s.size() - n, n));
    }
    return ans;

