// (false)
bool is_sorted(std::vector<long> lst) { 
    if(lst.size() == 0 || lst.size() == 1) {
        return true;
    }
    for(int i = 0; i < lst.size() - 1; i++) {
        if(lst[i] > lst[i+1]) {
            return false;
        }
        else if(lst[i] == lst[i+1]) {
            int j = i + 1;
            while(j < lst.size() - 1 && lst[j] == lst[j+1]) {
                j++;
            }
            if(j == lst.size() - 1) {
                return false;
            }
            i = j;
        }
    }
    return true;

