std::optional<long> prod_signs(std::vector<long> arr) { 
    if(arr.empty()) return std::nullopt;
    long sum = 0;
    long prod = 1;
    for(auto x : arr){
        if(x == 0) {
            prod = 0;
            break;
        }
        else if(x < 0) prod *= -1;
        else prod *= 1;
        sum += abs(x);
    }
    return sum * prod;

