// Write a function which sorts the given vector of integers
// in ascending order according to the sum of their digits.
// Note: if there are several items with similar sum of their digits,
// order them based on their index in original vector.
// For example:
// >>> order_by_points((std::vector<long>({(long)1, (long)11, (long)-1, (long)-11, (long)-12})))
// (std::vector<long>({(long)-1, (long)-11, (long)1, (long)-12, (long)11}))
// >>> order_by_points((std::vector<long>()))
// (std::vector<long>())
std::vector<long> order_by_points(std::vector<long> nums) { 
    std::vector<long> res;
    for(int i=0; i<nums.size(); i++){
        res.push_back(nums[i]);
    }
    sort(res.begin(), res.end(), [](const long &a, const long &b){
        int sum_a = 0, sum_b = 0;
        for(int i=a; i!=0; i/=10){
            sum_a += i%10;
        }
        for(int i=b; i!=0; i/=10){
            sum_b += i%10;
        }
        return (sum_a == sum_b) ? (a < b) : (sum_a < sum_b);
    });
    return res;

