// Given an integer. return a tuple that has the number of even and odd digits respectively.
// Example:
// >>> even_odd_count((-12))
// (std::make_tuple(1, 1))
// >>> even_odd_count((123))
// (std::make_tuple(1, 2))
std::tuple<long, long> even_odd_count(long num) { 
    int even=0;
    int odd=0;
    while(num){
        if(num%2==0)
            even++;
        else
            odd++;
        num=num/10;
    }
    return std::make_tuple(even,odd);

