std::string int_to_mini_roman(long number) { 
    std::map<long, std::string> m;
    m[1] = "i";
    m[4] = "iv";
    m[5] = "v";
    m[9] = "ix";
    m[10] = "x";
    m[40] = "xl";
    m[50] = "l";
    m[90] = "xc";
    m[100] = "c";
    m[400] = "cd";
    m[500] = "d";
    m[900] = "cm";
    m[1000] = "m";

    std::string result;
    while(number > 0){
        for(auto i : m){
            if(i.first <= number){
                result += i.second;
                number -= i.first;
                break;
            }
        }
    }
    return result;

