// 3
// >>> median([-10, 4, 6, 1000, 10, 20])
// 15.0
function median(l){ 
    l.sort(function(a,b){return a-b});
    var mid = Math.floor(l.length/2);
    if (l.length % 2 == 0) {
        return (l[mid-1] + l[mid]) / 2;
    } else {
        return l[mid];
    }
}<|endoftext|><|endoftext|>

