//brackets is a string of "<" and ">".
// return true if every opening bracket has a corresponding closing bracket.
// >>> correct_bracketing("<")
// false
// >>> correct_bracketing("<>")
// true
// >>> correct_bracketing("<<><>>")
// true
// >>> correct_bracketing("><<>")
// false
function correct_bracketing(brackets){ 
  let count = 0;
  for (let i = 0; i < brackets.length; i++) {
    if (brackets[i] === "<") {
      count++;
    } else if (brackets[i] === ">") {
      count--;
    }
    if (count < 0) {
      return false;
    }
  }
  return count === 0;
}<|endoftext|>

//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l){ 
    let inc = true;
    let dec = true;
    for(let i=0; i<l.length-1; i++){
        if(l[i] > l[i+1]){
            inc = false;
        }
        if(l[i] < l[i+1]){
            dec = false;
        }
    }
    return inc || dec;
}<|endoftext|><|endoftext|>

