// [1, 5, 653]
// >>> common([5, 3, 2, 8], [3, 2])
// [2, 3]
function common(l1, l2){ 
    let arr = [];
    for(let i=0; i<l1.length; i++){
        if(l2.includes(l1[i]) && !arr.includes(l1[i])){
            arr.push(l1[i]);
        }
    }
    return arr.sort((a,b) => a-b);
}

