// >>> total_match(["hi", "admin"], ["hi", "hi", "admin", "project"])
// ["hi", "admin"]
// >>> total_match(["hi", "admin"], ["hI", "hi", "hi"])
// ["hI", "hi", "hi"]
// >>> total_match(["4"], ["1", "2", "3", "4", "5"])
// ["4"]
function total_match(lst1, lst2){ 
    let sum1 = 0;
    let sum2 = 0;
    for (let i = 0; i < lst1.length; i++) {
        sum1 += lst1[i].length;
    }
    for (let j = 0; j < lst2.length; j++) {
        sum2 += lst2[j].length;
    }
    if (sum1 < sum2) {
        return lst1;
    } else if (sum2 < sum1) {
        return lst2;
    } else {
        return lst1;
    }
}<|endoftext|>

