// "lm"
// >>> encrypt("asdfghjkl")
// "ewhjklnop"
// >>> encrypt("gf")
// "kj"
// >>> encrypt("et")
// "ix"
function encrypt(s){ 
    let alphabet = 'abcdefghijklmnopqrstuvwxyz';
    let newAlphabet = '';
    for (let i = 0; i < alphabet.length; i++){
        if (i + 4 > alphabet.length - 1){
            newAlphabet += alphabet[i + 4 - alphabet.length];
        } else {
            newAlphabet += alphabet[i + 4];
        }
    }
    return s.split('').map((letter) => newAlphabet[alphabet.indexOf(letter)]).join('');
