// >>> histogram("a b c a b")
// {"a": 2, "b": 2}
// >>> histogram("b b b b a")
// {"b": 4}
// >>> histogram("")
// {}
function histogram(test){ 
    let obj = {};
    test.split(' ').forEach((el) => {
        if(obj[el]) obj[el]++;
        else obj[el] = 1;
    });
    let max = 0;
    for(let key in obj){
        if(obj[key] > max) max = obj[key];
    }
    let result = {};
    for(let key in obj){
        if(obj[key] === max) result[key] = obj[key];
    }
    return result;
}<|endoftext|>

