//Given an integer. return an array that has the number of even and odd digits respectively.
// Example:
// >>> even_odd_count(-12)
// [1, 1]
// >>> even_odd_count(123)
// [1, 2]
function even_odd_count(num){ 
    let count = {even:0, odd:0};
    num = Math.abs(num);
    while (num > 0) {
        if ((num % 2) === 0) {
            count.even++;
        } else {
            count.odd++;
        }
        num = Math.floor(num / 10);
    }
    return Object.values(count);
}<|endoftext|>

