// >>> eat(2, 11, 5)
// [7, 0]
// Variables:
// @number : integer
// the number of carrots that you have eaten.
// @need : integer
// the number of carrots that you need to eat.
// @remaining : integer
// the number of remaining carrots thet exist in stock
// Constrain:
// * 0 <= number <= 1000
// * 0 <= need <= 1000
// * 0 <= remaining <= 1000
// Have fun :)
function eat(number, need, remaining){ 
    let total = number + Math.min(need, remaining);
    let left = Math.max(0, remaining - need);
    return [total, left];
}<|endoftext|>

//Given two arrays operator, and operand. The first array has basic algebra operations, and 
// the second array is an array of integers. Use the two given arrays to build the algebric 
// expression and return the evaluation of this expression.
// The basic algebra operations:
// Addition ( + ) 
// Subtraction ( - ) 
// Multiplication ( * ) 
// Floor division ( // ) 
// Exponentiation ( ** ) 
// Example:
// operator['+', '*', '-']
// array = [2, 3, 4, 5]
// result = 2 + 3 * 4 - 5
// => result = 9
// Note:
// The length of operator array is equal to the length of operand array minus one.
// Operand is an array of of non-negative integers.
// Operator array has at least one operator, and operand array has at least two operands.
function do_algebra(operator, operand){ 
    let result = operand[0];
    for(let i=1; i<operand.length; i++){
        if(operator[i-1] == "+"){
            result += operand[i];
        }else if(operator[i-1] == "-"){
            result -= operand[i];
        }else if(operator[i-1] == "*"){
            result *= operand[i];
        }else if(operator[i-1] == "//"){
            result = Math.floor(result / operand[i]);
        }else if(operator[i-1] == "**"){
            result = Math.pow(result, operand[i]);
        }
    }
    return result;
}<|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|>

//You are given a string s.
// if s[i] is a letter, reverse its case from lower to upper or vise versa, 
// otherwise keep it as it is.
// If the string contains no letters, reverse the string.
// The function should return the resulted string.
// Examples
// >>> solve("1234")
// "4321"
// >>> solve("ab")
// "AB"
// >>> solve("#a@C")
// "#A@c"
function solve(s){ 
    let arr = s.split('')
    for(let i = 0; i < arr.length; i++){
        if(arr[i].match(/[a-zA-Z]/)){
            if(arr[i] === arr[i].toLowerCase()){
                arr[i] = arr[i].toUpperCase()
            } else {
                arr[i] = arr[i].toLowerCase()
            }
        }
    }
    return arr.join('')
}<|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|><|endoftext|>

//Given a string 'text', return its md5 hash equivalent string.
// If 'text' is an empty string, return undefined.
// >>> string_to_md5("Hello world")
// "3e25960a79dbc69b674cd4ec67a72c62"
function string_to_md5(text){ 
