//Check if in given array of numbers, are any two numbers closer to each other than
// given threshold.
// >>> has_close_elements([1.0, 2.0, 3.0], 0.5)
// false
// >>> has_close_elements([1.0, 2.8, 3.0, 4.0, 5.0, 2.0], 0.3)
// true
function has_close_elements(numbers, threshold){
    // sort the array
    numbers.sort((a,b) => a - b);
    // iterate through the array and check if any two consecutive elements are closer than threshold
    for (let i = 0; i < numbers.length - 1; i++){
        if (numbers[i+1] - numbers[i] < threshold) return true;
    }
    return false;
}
//Input to this function is a string containing multiple groups of nested parentheses. Your goal is to
// separate those group into separate strings and return the array of those.
// Separate groups are balanced (each open brace is properly closed) and not nested within each other
// Ignore any spaces in the input string.
// >>> separate_paren_groups("( ) (( )) (( )( ))")
// ["()", "(())", "(()())"]
function separate_paren_groups(paren_string){
    let result = [];
    let current_group = "";
    let stack = [];
    let i = 0;
    while(i < paren_string.length){
        let char = paren_string[i];
        if(char === " "){
            i++;
            continue;
        }
        if(char === "("){
            stack.push(char);
            current_group += char;
        }else if(char === ")"){
            stack.pop();
            current_group += char;
            if(stack.length === 0){
                result.push(current_group);
                current_group = "";
            }
        }
        i++;
    }
    return result;
}
//Given a positive floating point number, it can be decomposed into
// and integer part (largest integer smaller than given number) and decimals
// (leftover part always smaller than 1).
// Return the decimal part of the number.
// >>> truncate_number(3.5)
// 0.5
function truncate_number(number){
    return number - Math.floor(number);
}
//You're given an array of deposit and withdrawal operations on a bank account that starts with
// zero balance. Your task is to detect if at any point the balance of account fallls below zero, and
// at that point function should return true. Otherwise it should return false.
// >>> below_zero([1, 2, 3])
// false
// >>> below_zero([1, 2, -4, 5])
// true
function below_zero(operations){
    let balance = 0;
    for (let i = 0; i < operations.length; i++) {
        balance += operations[i];
        if (balance < 0) {
            return true;
        }
    }
    return false;
}
//For a given array of input numbers, calculate Mean Absolute Deviation
// around the mean of this dataset.
// Mean Absolute Deviation is the average absolute difference between each
// element and a centerpoint (mean in this case):
// MAD = average | x - x_mean |
// >>> mean_absolute_deviation([1.0, 2.0, 3.0, 4.0])
// 1.0
function mean_absolute_deviation(numbers){
    let sum = numbers.reduce((a,b) => a + b, 0);
    let mean = sum / numbers.length;
    let absoluteDiffs = numbers.map(x => Math.abs(x - mean));
    let sumAbsoluteDiffs = absoluteDiffs.reduce((a,b) => a + b, 0);
    return sumAbsoluteDiffs / numbers.length;
}
//Insert a number 'delimeter' between every two consecutive elements of input array `numbers'
// >>> intersperse([], 4)
// []
// >>> intersperse([1, 2, 3], 4)
// [1, 4, 2, 4, 3]
function intersperse(numbers, delimeter){
    let result = [];
    for (let i = 0; i < numbers.length; i++) {
        result.push(numbers[i]);
        if (i < numbers.length - 1) {
            result.push(delimeter);
        }
    }
    return result;
}
//Input to this function is a string represented multiple groups for nested parentheses separated by spaces.
// For each of the group, output the deepest level of nesting of parentheses.
// E.g. (()()) has maximum two levels of nesting while ((())) has three.
// >>> parse_nested_parens("(()()) ((())) () ((())()())")
// [2, 3, 1, 3]
function parse_nested_parens(paren_string){
    let groups = paren_string.split(' ');
    let result = [];
    for (let group of groups) {
        let maxDepth = 0;
        let currentDepth = 0;
        for (let char of group) {
            if (char === '(') {
                currentDepth++;
                maxDepth = Math.max(maxDepth, currentDepth);
            } else if (char === ')') {
                currentDepth--;
            }
        }
        result.push(maxDepth);
    }
    return result;
}
//Filter an input array of strings only for ones that contain given substring
// >>> filter_by_substring([], "a")
// []
// >>> filter_by_substring(["abc", "bacd", "cde", "array"], "a")
// ["abc", "bacd", "array"]
function filter_by_substring(strings, substring){
    let result = [];
    for (let i = 0; i < strings.length; i++) {
        if (strings[i].includes(substring)) {
            result.push(strings[i]);
        }
    }
    return result;
}
//For a given array of integers, return an array consisting of a sum and a product of all the integers in an array.
// Empty sum should be equal to 0 and empty product should be equal to 1.
// >>> sum_product([])
// [0, 1]
// >>> sum_product([1, 2, 3, 4])
// [10, 24]
function sum_product(numbers){
    let sum = 0;
    let product = 1;
    for (let i = 0; i < numbers.length; i++) {
        sum += numbers[i];
        product *= numbers[i];
    }
    return [sum, product];
}
//From a given array of integers, generate an array of rolling maximum element found until given moment
// in the sequence.
// >>> rolling_max([1, 2, 3, 2, 3, 4, 2])
// [1, 2, 3, 3, 3, 4, 4]
function rolling_max(numbers){
    let max = numbers[0];
    let result = [max];
    for (let i=1; i < numbers.length; i++){
        if (numbers[i] > max){
            max = numbers[i];
            result.push(max);
        } else {
            result.push(max);
        }
    }
    return result;
}
//Find the shortest palindrome that begins with a supplied string.
// Algorithm idea is simple:
// - Find the longest postfix of supplied string that is a palindrome.
// - Append to the end of the string reverse of a string prefix that comes before the palindromic suffix.
// >>> make_palindrome("")
// ""
// >>> make_palindrome("cat")
// "catac"
// >>> make_palindrome("cata")
// "catac"
function make_palindrome(string){
    let reversed = string.split('').reverse().join('');
    let i = 0;
    while(i < string.length){
        if(string.slice(0, string.length - i) === reversed.slice(0, string.length - i)){
            return string + reversed.slice(i);
        }
        i++;
    }
    return string + reversed;
}
//Input are two strings a and b consisting only of 1s and 0s.
// Perform binary XOR on these inputs and return result also as a string.
// >>> string_xor("010", "110")
// "100"
function string_xor(a, b){
    let result = "";
    for (let i = 0; i < a.length; i++) {
        if (a[i] === b[i]) {
            result += "0";
        } else {
            result += "1";
        }
    }
    return result;
}
//Out of array of strings, return the longest one. Return the first one in case of multiple
// strings of the same length. Return undefined in case the input array is empty.
// >>> longest([])
// undefined
// >>> longest(["a", "b", "c"])
// "a"
// >>> longest(["a", "bb", "ccc"])
// "ccc"
function longest(strings){
    if(strings.length === 0) return undefined;
    let longest = strings[0];
    for(let i=1; i<strings.length; i++){
        if(strings[i].length > longest.length){
            longest = strings[i];
        }
    }
    return longest;
}
//Return a greatest common divisor of two integers a and b
// >>> greatest_common_divisor(3, 5)
// 1
// >>> greatest_common_divisor(25, 15)
// 5
function greatest_common_divisor(a, b){
    if (b === 0) return a;
    return greatest_common_divisor(b, a % b);
}
