//Return median of elements in the array l.
// >>> median([3, 1, 2, 4, 5])
// 3
// >>> median([-10, 4, 6, 1000, 10, 20])
// 15.0
function median(l){
    let sorted = l.sort((a,b) => a - b);
    let mid = Math.floor(sorted.length / 2);
    if(sorted.length % 2 === 0){
        return (sorted[mid - 1] + sorted[mid])/2;
    }else{
        return sorted[mid];
    }
}
