//Check if two words have the same characters.
// >>> same_chars("eabcdzzzz", "dddzzzzzzzddeddabc")
// true
// >>> same_chars("abcd", "dddddddabc")
// true
// >>> same_chars("dddddddabc", "abcd")
// true
// >>> same_chars("eabcd", "dddddddabc")
// false
// >>> same_chars("abcd", "dddddddabce")
// false
// >>> same_chars("eabcdzzzz", "dddzzzzzzzddddabc")
// false
function same_chars(s0, s1){
    let count0 = {};
    let count1 = {};
    for (let c of s0) {
        count0[c] = (count0[c] || 0) + 1;
    }
    for (let c of s1) {
        count1[c] = (count1[c] || 0) + 1;
    }
    for (let key in count0) {
        if (count1[key] !== count0[key]) {
            return false;
        }
    }
    for (let key in count1) {
        if (count0[key] !== count1[key]) {
            return false;
        }
    }
    return true;
}
//Return n-th Fibonacci number.
// >>> fib(10)
// 55
// >>> fib(1)
// 1
// >>> fib(8)
// 21
function fib(n){
    if(n <= 1) return n;
    return fib(n-1)+fib(n-2);
}
//brackets is a string of "<" and ">".
// return true if every opening bracket has a corresponding closing bracket.
// >>> correct_bracketing("<")
// false
// >>> correct_bracketing("<>")
// true
// >>> correct_bracketing("<<><>>")
// true
// >>> correct_bracketing("><<>")
// false
function correct_bracketing(brackets){
    let count = 0;
    for (let i = 0; i < brackets.length; i++) {
        if (brackets[i] === "<") {
            count++;
        } else {
            count--;
        }
        if (count < 0) {
            return false;
        }
    }
    return count === 0;
}
//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l){
    let increasing = true;
    let decreasing = true;
    for (let i = 1; i < l.length; i++) {
        if (l[i] > l[i-1]) {
            decreasing = false;
        } else if (l[i] < l[i-1]) {
            increasing = false;
        } else {
            // equal, so both are still possible
        }
    }
    return increasing || decreasing;
}
//Return sorted unique common elements for two arrays.
// >>> common([1, 4, 3, 34, 653, 2, 5], [5, 7, 1, 5, 9, 653, 121])
// [1, 5, 653]
// >>> common([5, 3, 2, 8], [3, 2])
// [2, 3]
function common(l1, l2){
    let set1 = new Set(l1);
    let set2 = new Set(l2);
    let commonSet = new Set([...set1].filter(x => set2.has(x)));
    return [...commonSet].sort((a,b) => a - b);
}
//Return the largest prime factor of n. Assume n > 1 and is not a prime.
// >>> largest_prime_factor(13195)
// 29
// >>> largest_prime_factor(2048)
// 2
function largest_prime_factor(n){
    let factor = 2;
    while(n % factor === 0){
        n /= factor;
    }
    factor = 3;
    while(factor * factor <= n){
        while(n % factor === 0){
            n /= factor;
        }
        factor += 2;
    }
    return n;
}
