//brackets is a string of "(" and ")".
// return true if every opening bracket has a corresponding closing bracket.
// >>> correct_bracketing("(")
// false
// >>> correct_bracketing("()")
// true
// >>> correct_bracketing("(()())")
// true
// >>> correct_bracketing(")(()")
// false
function correct_bracketing(brackets){
    let stack = [];
    for (let i = 0; i < brackets.length; i++) {
        if (brackets[i] === '(') {
            stack.push(brackets[i]);
        } else {
            if (stack.length === 0) {
                return false;
            } else {
                stack.pop();
            }
        }
    }
    return stack.length === 0;
}
//xs represent coefficients of a polynomial.
// xs[0] + xs[1] * x + xs[2] * x^2 + ....
// Return derivative of this polynomial in the same form.
// >>> derivative([3, 1, 2, 4, 5])
// [1, 4, 12, 20]
// >>> derivative([1, 2, 3])
// [2, 6]
function derivative(xs){
    let result = [];
    for (let i = 1; i < xs.length; i++) {
        result.push(xs[i] * i);
    }
    return result;
}
//The FibFib number sequence is a sequence similar to the Fibbonacci sequnece that's defined as follows:
// fibfib(0) == 0
// fibfib(1) == 0
// fibfib(2) == 1
// fibfib(n) == fibfib(n-1) + fibfib(n-2) + fibfib(n-3).
// Please write a function to efficiently compute the n-th element of the fibfib number sequence.
// >>> fibfib(1)
// 0
// >>> fibfib(5)
// 4
// >>> fibfib(8)
// 24
function fibfib(n){
    if(n === 0 || n === 1){
        return 0;
    }
    if(n === 2){
        return 1;
    }
    let dp = new Array(n+1);
    dp[0] = 0;
    dp[1] = 0;
    dp[2] = 1;
    for(let i=3; i<=n; i++){
        dp[i] = dp[i-1] + dp[i-2] + dp[i-3];
    }
    return dp[n];
}
//Write a function vowels_count which takes a string representing
// a word as input and returns the number of vowels in the string.
// Vowels in this case are 'a', 'e', 'i', 'o', 'u'. Here, 'y' is also a
// vowel, but only when it is at the end of the given word.
// Example:
// >>> vowels_count("abcde")
// 2
// >>> vowels_count("ACEDY")
// 3
function vowels_count(s){
    let count = 0;
    let vowels = ['a','e','i','o','u','y'];
    for (let i = 0; i < s.length; i++) {
        if (vowels.includes(s[i].toLowerCase())){
            count++;
        }
    }
    return count;
}
//Circular shift the digits of the integer x, shift the digits right by shift
// and return the result as a string.
// If shift > number of digits, return digits reversed.
// >>> circular_shift(12, 1)
// "21"
// >>> circular_shift(12, 2)
// "12"
function circular_shift(x, shift){
    let digits = x.toString().split('')
    let numDigits = digits.length
    if (shift > numDigits) {
        return digits.reverse().join('')
    } else {
        let shifted = digits.slice(-shift).concat(digits.slice(0,-shift))
        return shifted.join('')
    }
}
//Task
// Write a function that takes a string as input and returns the sum of the upper characters only'
// ASCII codes.
// Examples:
// >>> digitSum("")
// 0
// >>> digitSum("abAB")
// 131
// >>> digitSum("abcCd")
// 67
// >>> digitSum("helloE")
// 69
// >>> digitSum("woArBld")
// 131
// >>> digitSum("aAaaaXa")
// 153
function digitSum(s){
    let sum = 0;
    for (let i = 0; i < s.length; i++) {
        let char = s[i];
        if (char === char.toUpperCase()) {
            sum += char.charCodeAt(0);
        }
    }
    return sum;
}
//In this task, you will be given a string that represents a number of apples and oranges 
// that are distributed in a basket of fruit this basket contains 
// apples, oranges, and mango fruits. Given the string that represents the total number of 
// the oranges and apples and an integer that represent the total number of the fruits 
// in the basket return the number of the mango fruits in the basket.
// for examble:
// >>> fruit_distribution("5 apples and 6 oranges", 19)
// 8
// >>> fruit_distribution("0 apples and 1 oranges", 3)
// 2
// >>> fruit_distribution("2 apples and 3 oranges", 100)
// 95
// >>> fruit_distribution("100 apples and 1 oranges", 120)
// 19
function fruit_distribution(s, n){
    //split the string into parts
    let parts = s.split(' ');
    //get the number of apples and oranges
    let apples = parseInt(parts[0]);
    let oranges = parseInt(parts[3]);
    //calculate the number of mangoes
    let mangoes = n - apples - oranges;
    return mangoes;
}
//"Given an array representing a branch of a tree that has non-negative integer nodes
// your task is to pluck one of the nodes and return it.
// The plucked node should be the node with the smallest even value.
// If multiple nodes with the same smallest even value are found return the node that has smallest index.
// The plucked node should be returned in an array, [ smalest_value, its index ],
// If there are no even values or the given array is empty, return [].
// Example 1:
// >>> pluck([4, 2, 3])
// [2, 1]
// Explanation: 2 has the smallest even value, and 2 has the smallest index.
// Example 2:
// >>> pluck([1, 2, 3])
// [2, 1]
// Explanation: 2 has the smallest even value, and 2 has the smallest index.
// Example 3:
// >>> pluck([])
// []
// Example 4:
// >>> pluck([5, 0, 3, 0, 4, 2])
// [0, 1]
// Explanation: 0 is the smallest value, but  there are two zeros,
// so we will choose the first zero, which has the smallest index.
// Constraints:
// * 1 <= nodes.length <= 10000
// * 0 <= node.value
function pluck(arr){
    let minEven = null;
    let minIndex = -1;
    for (let i=0; i<arr.length; i++){
        if (arr[i] % 2 === 0){
            if (minEven === null || arr[i] < minEven || (arr[i] === minEven && i < minIndex)){
                minEven = arr[i];
                minIndex = i;
            }
        }
    }
    if (minEven === null) return [];
    return [minEven, minIndex];
}
//You are given a non-empty array of positive integers. Return the greatest integer that is greater than 
// zero, and has a frequency greater than or equal to the value of the integer itself. 
// The frequency of an integer is the number of times it appears in the array.
// If no such a value exist, return -1.
// Examples:
// >>> search([4, 1, 2, 2, 3, 1])
// 2
// >>> search([1, 2, 2, 3, 3, 3, 4, 4, 4])
// 3
// >>> search([5, 5, 4, 4, 4])
// -1
function search(lst){
    let freq = {};
    for (let num of lst) {
        if (freq[num]) {
            freq[num]++;
        } else {
            freq[num] = 1;
        }
    }
    for (let key in freq) {
        if (freq[key] >= key) {
            return key;
        }
    }
    return -1;
}
//Given array of integers, return array in strange order.
// Strange sorting, is when you start with the minimum value,
// then maximum of the remaining integers, then minimum and so on.
// Examples:
// >>> strange_sort_list([1, 2, 3, 4])
// [1, 4, 2, 3]
// >>> strange_sort_list([5, 5, 5, 5])
// [5, 5, 5, 5]
// >>> strange_sort_list([])
// []
function strange_sort_list(lst){
    let sorted = lst.sort((a,b) => a - b);
    let result = [];
    let i = 0;
    while(i < lst.length){
        result.push(sorted[i]);
        i++;
        if(i < lst.length){
            result.push(sorted[lst.length - 1 - i]);
            i++;
        }
    }
    return result;
}
//Given the lengths of the three sides of a triangle. Return the area of
// the triangle rounded to 2 decimal points if the three sides form a valid triangle. 
// Otherwise return -1
// Three sides make a valid triangle when the sum of any two sides is greater 
// than the third side.
// Example:
// >>> triangle_area(3, 4, 5)
// 6.0
// >>> triangle_area(1, 2, 10)
// -1
function triangle_area(a, b, c){
    //Check if the three sides form a valid triangle
    if (a + b > c && a + c > b && b + c > a) {
        //Calculate the area using Heron's formula
        let s = (a + b + c)/2;
        let area = Math.sqrt(s*(s-a)*(s-b)*(s-c));
        return Math.round(area * 100)/100;
    } else {
        return -1;
    }
}
//Write a function that returns true if the object q will fly, and false otherwise.
// The object q will fly if it's balanced (it is a palindromic array) and the sum of its elements is less than or equal the maximum possible weight w.
// Example:
// >>> will_it_fly([1, 2], 5)
// false
// # 1+2 is less than the maximum possible weight, but it's unbalanced.
// >>> will_it_fly([3, 2, 3], 1)
// false
// # it's balanced, but 3+2+3 is more than the maximum possible weight.
// >>> will_it_fly([3, 2, 3], 9)
// true
// # 3+2+3 is less than the maximum possible weight, and it's balanced.
// >>> will_it_fly([3], 5)
// true
// # 3 is less than the maximum possible weight, and it's balanced.
function will_it_fly(q, w){
    let sum = q.reduce((a,b) => a + b, 0);
    let isBalanced = q.toString() === q.reverse().toString();
    return isBalanced && sum <= w;
}
//Given an array arr of integers, find the minimum number of elements that
// need to be changed to make the array palindromic. A palindromic array is an array that
// is read the same backwards and forwards. In one change, you can change one element to any other element.
// For example:
// >>> smallest_change([1, 2, 3, 5, 4, 7, 9, 6])
// 4
// >>> smallest_change([1, 2, 3, 4, 3, 2, 2])
// 1
// >>> smallest_change([1, 2, 3, 2, 1])
// 0
function smallest_change(arr){
    let left = 0;
    let right = arr.length - 1;
    let count = 0;
    while(left < right){
        if(arr[left] !== arr[right]){
            count++;
        }
        left++;
        right--;
    }
    return count;
}
//Write a function that accepts two arrays of strings and returns the array that has 
// total number of chars in the all strings of the array less than the other array.
// if the two arrays have the same number of chars, return the first array.
// Examples
// >>> total_match([], [])
// []
// >>> total_match(["hi", "admin"], ["hI", "Hi"])
// ["hI", "Hi"]
// >>> total_match(["hi", "admin"], ["hi", "hi", "admin", "project"])
// ["hi", "admin"]
// >>> total_match(["hi", "admin"], ["hI", "hi", "hi"])
// ["hI", "hi", "hi"]
// >>> total_match(["4"], ["1", "2", "3", "4", "5"])
// ["4"]
function total_match(lst1, lst2){
    let total1 = lst1.reduce((sum, str) => sum + str.length, 0);
    let total2 = lst2.reduce((sum, str) => sum + str.length, 0);
    if(total1 < total2) return lst1;
    else if(total1 > total2) return lst2;
    else return lst1;
}
//Write a function that returns true if the given number is the multiplication of 3 prime numbers
// and false otherwise.
// Knowing that (a) is less then 100. 
// Example:
// >>> is_multiply_prime(30)
// true
// 30 = 2 * 3 * 5
function is_multiply_prime(a){
    // your code here
    let prime_factors = [];
    let i = 2;
    while (i <= a) {
        while (a % i === 0) {
            prime_factors.push(i);
            a /= i;
        }
        i++;
    }
    if(prime_factors.length === 3) {
        return true;
    } else {
        return false;
    }
}
//Your task is to write a function that returns true if a number x is a simple
// power of n and false in other cases.
// x is a simple power of n if n**int=x
// For example:
// >>> is_simple_power(1, 4)
// true
// >>> is_simple_power(2, 2)
// true
// >>> is_simple_power(8, 2)
// true
// >>> is_simple_power(3, 2)
// false
// >>> is_simple_power(3, 1)
// false
// >>> is_simple_power(5, 3)
// false
function is_simple_power(x, n){
    if (x === 1) return true;
    if (x < 1) return false;
    let pow = n;
    while (pow <= x) {
        if (pow === x) return true;
        pow *= n;
    }
    return false;
}
