// "lm"
// >>> encrypt("asdfghjkl")
// "ewhjklnop"
// >>> encrypt("gf")
// "kj"
// >>> encrypt("et")
// "ix"
function encrypt(s){
    let alphabet = 'abcdefghijklmnopqrstuvwxyz';
    let encrypted = '';
    for (let i = 0; i < s.length; i++) {
