// 3
function count_nums(arr){
    let count = 0;
    for (let i = 0; i < arr.length; i++) {
        let num = arr[i];
        let sum = 0;
        let isNegative = false;
        if (num < 0) {
            isNegative = true;
            num = -num;
        }
        while (num > 0) {
            sum += num % 10;
            num = Math.floor(num / 10);
        }
        if (isNegative) {
            sum = -sum;
        }
        if (sum > 0) {
            count++;
        }
    }
    return count;
}
