// {"a": 1, "b": 1, "c": 1}
// >>> histogram("a b b a")
// {"a": 2, "b": 2}
// >>> histogram("a b c a b")
// {"a": 2, "b": 2}
// >>> histogram("b b b b a")
// {"b": 4}
// >>> histogram("")
// {}
function histogram(test){
    let obj = {};
    let arr = test.split(" ");
    let max = 0;
    let maxLetters = [];
    for (let i = 0; i < arr.length; i++) {
        let letter = arr[i];
        if (obj[letter]) {
            obj[letter]++;
        } else {
            obj[letter] = 1;
        }
    }
    let maxCount = 0;
    for (let key in obj) {
        if (obj[key] > maxCount) {
            maxCount = obj[key];
        }
    }
    for (let key in obj) {
        if (obj[key] === maxCount) {
            maxLetters.push(key);
        }
    }
    let result = {};
    for (let i = 0; i < maxLetters.length; i++) {
        result[maxLetters[i]] = maxCount;
    }
    return result;
}
