//Given a positive integer n, return a sorted array that has the odd numbers in collatz sequence.
// The Collatz conjecture is a conjecture in mathematics that concerns a sequence defined
// as follows: start with any positive integer n. Then each term is obtained from the 
// previous term as follows: if the previous term is even, the next term is one half of 
// the previous term. If the previous term is odd, the next term is 3 times the previous
// term plus 1. The conjecture is that no matter what value of n, the sequence will always reach 1.
// Note: 
// 1. Collatz(1) is [1].
// 2. returned array sorted in increasing order.
// For example:
// get_odd_collatz(5) returns [1, 5] # The collatz sequence for 5 is [5, 16, 8, 4, 2, 1], so the odd numbers are only 1, and 5.
// >>> get_odd_collatz(5)
// [1, 5]
function get_odd_collatz(n){
    let arr = [];
    let num = n;
    while(num !== 1){
        if(num % 2 === 1){
            arr.push(num);
            num = num * 3 + 1;
        } else {
            num = num / 2;
        }
    }
    arr.push(1);
    arr.sort((a,b) => a - b);
    return arr;
}
//You have to write a function which validates a given date string and
// returns true if the date is valid otherwise false.
// The date is valid if all of the following rules are satisfied:
// 1. The date string is not empty.
// 2. The number of days is not less than 1 or higher than 31 days for months 1,3,5,7,8,10,12. And the number of days is not less than 1 or higher than 30 days for months 4,6,9,11. And, the number of days is not less than 1 or higher than 29 for the month 2.
// 3. The months should not be less than 1 or higher than 12.
// 4. The date should be in the format: mm-dd-yyyy
// >>> valid_date("03-11-2000")
// true
// >>> valid_date("15-01-2012")
// false
// >>> valid_date("04-0-2040")
// false
// >>> valid_date("06-04-2020")
// true
// >>> valid_date("06/04/2020")
// false
function valid_date(date){
    // write code here
    // check if date is empty
    if(date.length === 0) return false;
    // check if date is in mm-dd-yyyy format
    if(date.split('-').length !== 3) return false;
    // split date into parts
    let parts = date.split('-');
    // check if month is between 1-12
    if(parts[0] < 1 || parts[0] > 12) return false;
    // check if day is between 1-31, 1-30, or 1-29 depending on month
    let month = parts[0];
    let day = parts[1];
    if(month === 2){
        if(day < 1 || day > 29) return false;
    } else if(month === 4 || month === 6 || month === 9 || month === 11){
        if(day < 1 || day > 30) return false;
    } else {
        if(day < 1 || day > 31) return false;
    }
    // check if year is valid
    if(parts[2].length < 4) return false;
    // check if day is valid
    if(parts[1] < 1 || parts[1] > 31) return false;
    return true;
}
//Given a string of words, return an array of words split on whitespace, if no whitespaces exists in the text you
// should split on commas ',' if no commas exists you should return the number of lower-case letters with odd order in the
// alphabet, ord('a') = 0, ord('b') = 1, ... ord('z') = 25
// Examples
// >>> split_words("Hello world!")
// ["Hello", "world!"]
// >>> split_words("Hello,world!")
// ["Hello", "world!"]
// >>> split_words("abcdef")
// 3
function split_words(txt){
    if(txt.includes(' ')){
        return txt.split(' ');
    }else if(txt.includes(',')){
        return txt.split(',');
    }else{
        let count = 0;
        for(let i=0; i<txt.length; i++){
            if(txt[i].toLowerCase() >= 'a' && txt[i].toLowerCase() <= 'z'){
                let ord = txt[i].toLowerCase().charCodeAt(0) - 'a'.charCodeAt(0);
                if(ord % 2 === 0){
                    count++;
                }
            }
        }
        return count;
    }
}
