// false
// >>> is_nested("[[][]]")
// true
// >>> is_nested("[[]][[")
// true
function is_nested(string){
    let stack = [];
    let nested = false;
    for (let i = 0; i < string.length; i++) {
        if (string[i] === '[') {
            stack.push(i);
        } else {
            if (stack.length > 0) {
                let index = stack.pop();
                if (stack.length > 0) {
                    nested = true;
                }
            }
        }
    }
    return nested;
}
