//The Brazilian factorial is defined as:
// brazilian_factorial(n) = n! * (n-1)! * (n-2)! * ... * 1!
// where n > 0
// For example:
// >>> special_factorial(4)
// 288
// The function will receive an integer as input and should return the special
// factorial of this integer.
function special_factorial(n){
    let result = 1;
    for (let i = 1; i <= n; i++) {
        result *= factorial(i);
    }
    return result;
}
//Given a string text, replace all spaces in it with underscores, 
// and if a string has more than 2 consecutive spaces, 
// then replace all consecutive spaces with - 
// >>> fix_spaces(" Example")
// "Example"
// >>> fix_spaces(" Example 1")
// "Example_1"
// >>> fix_spaces(" Example 2")
// "_Example_2"
// >>> fix_spaces(" Example 3")
// "_Example-3"
function fix_spaces(text){
    let arr = text.split(' ');
    let result = '';
    let count = 0;
    for (let i = 0; i < arr.length; i++){
        if (arr[i] === ''){
            count++;
        } else {
            if (count > 0){
                if (count > 2){
                    result += '-';
                } else {
                    result += '_'.repeat(count);
                }
                count = 0;
            }
            result += arr[i];
        }
    }
    if (count > 0){
        if (count > 2){
            result += '-';
        } else {
            result += '_'.repeat(count);
        }
    }
    return result;
}
//Create a function which takes a string representing a file's name, and returns
// 'Yes' if the the file's name is valid, and returns 'No' otherwise.
// A file's name is considered to be valid if and only if all the following conditions 
// are met:
// - There should not be more than three digits ('0'-'9') in the file's name.
// - The file's name contains exactly one dot '.'
// - The substring before the dot should not be empty, and it starts with a letter from 
// the latin alphapet ('a'-'z' and 'A'-'Z').
// - The substring after the dot should be one of these: ['txt', 'exe', 'dll']
// Examples:
// >>> file_name_check("example.txt")
// "Yes"
// >>> file_name_check("1example.dll")
// "No"
function file_name_check(file_name){
    // your code here
    let digits = 0;
    let dotIndex = file_name.indexOf('.');
    if(dotIndex === -1) return 'No';
    let beforeDot = file_name.slice(0, dotIndex);
    let afterDot = file_name.slice(dotIndex+1);
    if(beforeDot === '') return 'No';
    if(!beforeDot.match(/^[a-zA-Z]/)) return 'No';
    if(afterDot.length !== 3) return 'No';
    if(!['txt', 'exe', 'dll'].includes(afterDot)) return 'No';
    for(let i=0; i<beforeDot.length; i++){
        if(/[0-9]/.test(beforeDot[i])) digits++;
    }
    if(digits > 3) return 'No';
    return 'Yes';
}
